import React, { useState, useEffect } from 'react'
import { supabase } from '../utils/supabaseClient'
import { Droplet, Plus } from 'lucide-react'
import './FormRecoleccion.css'

export default function FormRecoleccion() {
  const [lugares, setLugares] = useState([])
  const [lugarActual, setLugarActual] = useState(null)
  const [bidones, setBidones] = useState([{ id: 1, cantidad: '' }])
  const [totalLugares, setTotalLugares] = useState(0)
  const [lugarActualIndex, setLugarActualIndex] = useState(0)
  const [loading, setLoading] = useState(false)
  const [mensaje, setMensaje] = useState('')

  useEffect(() => {
    cargarLugares()
  }, [])

  const cargarLugares = async () => {
    const { data, error } = await supabase
      .from('lugares')
      .select('*')
      .order('id')

    if (error) {
      console.error('Error:', error)
      return
    }

    if (data && data.length > 0) {
      setLugares(data)
      setLugarActual(data[0])
      setTotalLugares(data.length)
    }
  }

  const handleBidonChange = (index, valor) => {
    const nuevosBidones = [...bidones]
    nuevosBidones[index].cantidad = valor

    // Agregar nuevo bid√≥n si complet√≥ el √∫ltimo
    if (valor && index === bidones.length - 1) {
      nuevosBidones.push({
        id: bidones.length + 1,
        cantidad: ''
      })
    }

    setBidones(nuevosBidones)
  }

  const eliminarBidon = (index) => {
    if (bidones.length > 1) {
      const nuevosBidones = bidones.filter((_, i) => i !== index)
      
      // Renumerar bidones
      nuevosBidones.forEach((bidon, i) => {
        bidon.id = i + 1
      })
      
      setBidones(nuevosBidones)
    }
  }

  const calcularTotal = () => {
    return bidones.reduce((sum, bidon) => {
      return sum + (parseFloat(bidon.cantidad) || 0)
    }, 0)
  }

  const guardarRecoleccion = async () => {
    if (!lugarActual) {
      setMensaje('‚ùå Selecciona un lugar')
      return
    }

    const bidonesConCantidad = bidones.filter(
      (b) => b.cantidad && parseFloat(b.cantidad) > 0
    )

    if (bidonesConCantidad.length === 0) {
      setMensaje('‚ùå Ingresa al menos una cantidad')
      return
    }

    setLoading(true)
    setMensaje('')

    try {
      // Guardar cada bid√≥n como registro
      for (const bidon of bidonesConCantidad) {
        const { error } = await supabase.from('recepciones').insert([
          {
            lugar_id: lugarActual.id,
            tipo_aceite: 'Aceite Quemado',
            cantidad: parseFloat(bidon.cantidad),
            observaciones: `Bid√≥n #${bidon.id}`,
            fecha_recepcion: new Date().toISOString(),
            recolector_nombre: null
          }
        ])

        if (error) throw error
      }

      setMensaje(
        `‚úÖ ${bidonesConCantidad.length} bid√≥n(es) guardado(s) en ${lugarActual.nombre}`
      )

      // Ir al siguiente lugar
      setTimeout(() => {
        if (lugarActualIndex < totalLugares - 1) {
          const nuevoIndex = lugarActualIndex + 1
          setLugarActualIndex(nuevoIndex)
          setLugarActual(lugares[nuevoIndex])
          setBidones([{ id: 1, cantidad: '' }])
          setMensaje('')
        } else {
          setMensaje('‚úÖ ¬°Recolecci√≥n completada en todos los lugares!')
          setTimeout(() => {
            setLugarActualIndex(0)
            setLugarActual(lugares[0])
            setBidones([{ id: 1, cantidad: '' }])
            setMensaje('')
          }, 2000)
        }
      }, 1500)
    } catch (error) {
      setMensaje('‚ùå Error: ' + error.message)
    }

    setLoading(false)
  }

  const irAnterior = () => {
    if (lugarActualIndex > 0) {
      const nuevoIndex = lugarActualIndex - 1
      setLugarActualIndex(nuevoIndex)
      setLugarActual(lugares[nuevoIndex])
      setBidones([{ id: 1, cantidad: '' }])
      setMensaje('')
    }
  }

  if (!lugarActual) {
    return (
      <div className="form-container">
        <p>Cargando lugares...</p>
      </div>
    )
  }

  const total = calcularTotal()
  const porcentajeProgreso = ((lugarActualIndex + 1) / totalLugares) * 100

  return (
    <div className="form-container">
      <div className="form-header">
        <Droplet size={32} color="#2563eb" />
        <div>
          <h1>Control de Aceite Quemado</h1>
          <p className="info-lugar">
            Lugar {lugarActualIndex + 1} de {totalLugares}
          </p>
        </div>
      </div>

      {/* UBICACI√ìN ACTUAL */}
      <div className="lugar-actual">
        <h2>{lugarActual.nombre}</h2>
        <span className="codigo">C√≥digo: {lugarActual.codigo_tienda}</span>
      </div>

      {/* MENSAJE */}
      {mensaje && (
        <div
          className={`mensaje ${
            mensaje.includes('‚ùå') ? 'error' : 'exito'
          }`}
        >
          {mensaje}
        </div>
      )}

      {/* TIPO ACEITE FIJO */}
      <div className="aceite-tipo">
        <strong>Tipo:</strong> Aceite Quemado
      </div>

      {/* BIDONES */}
      <div className="bidones-seccion">
        <label className="label-libras">Libras</label>

        {bidones.map((bidon, index) => (
          <div key={index} className="fila-bidon">
            <span className="nombre-bidon">Bid√≥n {bidon.id}</span>
            <input
              type="number"
              step="0.1"
              min="0"
              value={bidon.cantidad}
              onChange={(e) =>
                handleBidonChange(index, e.target.value)
              }
              placeholder="0"
              className="input-cantidad"
            />
            {bidones.length > 1 && (
              <button
                type="button"
                onClick={() => eliminarBidon(index)}
                className="btn-eliminar"
                title="Eliminar"
              >
                ‚úï
              </button>
            )}
          </div>
        ))}
      </div>

      {/* TOTAL */}
      <div className="total-recolectado">
        <span>Total Recolectado:</span>
        <span className="valor-total">{total.toFixed(2)} lbs</span>
      </div>

      {/* BOTONES */}
      <div className="botones-accion">
        <button
          onClick={irAnterior}
          disabled={lugarActualIndex === 0 || loading}
          className="btn-anterior"
        >
          ‚Üê Anterior
        </button>

        <button
          onClick={guardarRecoleccion}
          disabled={loading || total === 0}
          className="btn-guardar"
        >
          {loading ? '‚è≥ Guardando...' : 'üíæ Guardar y Siguiente'}
        </button>
      </div>

      {/* BARRA PROGRESO */}
      <div className="barra-progreso">
        <div
          className="progreso"
          style={{ width: `${porcentajeProgreso}%` }}
        ></div>
      </div>
    </div>
  )
}
